CREATE TABLE BRAND (ID BIGINT AUTO_INCREMENT NOT NULL, NOMBREMARCA VARCHAR(255), ORIGEN VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE CAR (ID BIGINT AUTO_INCREMENT NOT NULL, ANIO BIGINT, NOMBRE VARCHAR(255), TIPO VARCHAR(255), BRAND_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE REQUEST (ID BIGINT AUTO_INCREMENT NOT NULL, DEPARTURE_DATE DATE, DESTINO VARCHAR(255), ORIGEN VARCHAR(255), TRACKNUMBER BIGINT, TAXIDRIVER_ID BIGINT, USER_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE ROLE (ID BIGINT AUTO_INCREMENT NOT NULL, NIVELACCESSO TINYINT, NOMBREROL VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE TAXI (ID BIGINT AUTO_INCREMENT NOT NULL, PLACA VARCHAR(255), CAR_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE TAXIDRIVER (ID BIGINT AUTO_INCREMENT NOT NULL, LICENCIA VARCHAR(255), TAXI_ID BIGINT, USER_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE USER (ID BIGINT AUTO_INCREMENT NOT NULL, APELLIDOMATERNO VARCHAR(255), APELLIDOPATERNO VARCHAR(255), CORREOELECTRONICO VARCHAR(255), NOMBRE VARCHAR(255), PASSWD VARCHAR(255), TELEFONO BIGINT, ROLE_ID BIGINT, ADDRESS_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE TAXIQUEUE (ID BIGINT AUTO_INCREMENT NOT NULL, HORALLEGADA TIME, TAXIDRIVER_ID BIGINT, REQUEST_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE ADDRESS (ID BIGINT AUTO_INCREMENT NOT NULL, CALLE VARCHAR(255), COLONIA VARCHAR(255), DELEGACION VARCHAR(255), ESTADO VARCHAR(255), NUMERO INTEGER, PRIMARY KEY (ID))
CREATE TABLE WEEKLYTAX (ID BIGINT AUTO_INCREMENT NOT NULL, TAX DOUBLE, TAXIDRIVER_ID BIGINT, PRIMARY KEY (ID))
ALTER TABLE CAR ADD CONSTRAINT FK_CAR_BRAND_ID FOREIGN KEY (BRAND_ID) REFERENCES BRAND (ID)
ALTER TABLE REQUEST ADD CONSTRAINT FK_REQUEST_TAXIDRIVER_ID FOREIGN KEY (TAXIDRIVER_ID) REFERENCES TAXIDRIVER (ID)
ALTER TABLE REQUEST ADD CONSTRAINT FK_REQUEST_USER_ID FOREIGN KEY (USER_ID) REFERENCES USER (ID)
ALTER TABLE TAXI ADD CONSTRAINT FK_TAXI_CAR_ID FOREIGN KEY (CAR_ID) REFERENCES CAR (ID)
ALTER TABLE TAXIDRIVER ADD CONSTRAINT FK_TAXIDRIVER_USER_ID FOREIGN KEY (USER_ID) REFERENCES USER (ID)
ALTER TABLE TAXIDRIVER ADD CONSTRAINT FK_TAXIDRIVER_TAXI_ID FOREIGN KEY (TAXI_ID) REFERENCES TAXI (ID)
ALTER TABLE USER ADD CONSTRAINT FK_USER_ROLE_ID FOREIGN KEY (ROLE_ID) REFERENCES ROLE (ID)
ALTER TABLE USER ADD CONSTRAINT FK_USER_ADDRESS_ID FOREIGN KEY (ADDRESS_ID) REFERENCES ADDRESS (ID)
ALTER TABLE TAXIQUEUE ADD CONSTRAINT FK_TAXIQUEUE_TAXIDRIVER_ID FOREIGN KEY (TAXIDRIVER_ID) REFERENCES TAXIDRIVER (ID)
ALTER TABLE TAXIQUEUE ADD CONSTRAINT FK_TAXIQUEUE_REQUEST_ID FOREIGN KEY (REQUEST_ID) REFERENCES REQUEST (ID)
ALTER TABLE WEEKLYTAX ADD CONSTRAINT FK_WEEKLYTAX_TAXIDRIVER_ID FOREIGN KEY (TAXIDRIVER_ID) REFERENCES TAXIDRIVER (ID)
